var records = {!GETRECORDIDS( $ObjectType.Brackets_Team__c )} ;
var oName = 'Team';
var error = false;
var msj = '';
if( records.length < 1 ){
   error = true;
   msj = 'Please select at least one Team!'; 
}

if( error ) {
       alert( msj );    
}
else{
       location.href = '/apex/ExportTeams?refId='+records+'&refType='+oName;
}
